package pgmString;

public class Stringformat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(String.format("String - %s", "pradeep"));
		System.out.println(String.format("char - %c", 'a'));
		System.out.println(String.format("float - %f", 11.10));
		System.out.println(String.format("bool - %b", true));
	}

}
/*
String - pradeep
char - a
float - 11.100000
bool - true
*/