package pgmString;

public class StringlastIndexOf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String para = "Auto-generated method stub";
		
		System.out.println(para.lastIndexOf("e")); //last element's index
		//16
		System.out.print(para.lastIndexOf("e",15)); //last element's index within boundary
		//12
	}
}
